import React from 'react'
import { useState } from 'react'
import '../node_modules/bootstrap/dist/css/bootstrap.css'

export default function App(){
    const[user, setUser]=useState(['zoro','nancy','peter','queen'])
    const updateArr=()=>{
        setUser(['one','two','nancy','zoro'])
    }
    const person={
        fname:'ms',
        lname:'dhoni'
    }
    const person1={
        fname:'virat',
        lname:'kholi'
    }
    const[user1, setUSer1]=useState(person)
    const updateObject=()=>{
        setUSer1(person1)
    }
    return(
        <>
        <h1>App Component </h1>
        <h3 className='pl-4'>{user}</h3>
        <h3>{user1.fname}</h3>
        <button className='btn btn-primary p-5' onClick={updateArr}>update array</button>
        <button className='btn btn-primary' onClick={updateObject}>update array</button>
        </>
    )
}
