    const[state, setState]=useState(pic)
    const handler=()=>{
        setState(pic2)
    }

    return(
        <>
        <h1>App Component </h1>
        <img cl src={state}></img>
        <button onClick={handler}>update image</button>
        </>
    )
}
export default App
