import React from 'react'
import { useState } from 'react'
import '../node_modules/bootstrap/dist/css/bootstrap.css'

export default function App(){
    const person={
        fname:'MS',
        lname:'dhoni'
    }

    const[user1, setUser1]=useState(person)
    const updateObject=()=>{
        setUser1({fname:'bontal',lname:'kumar'})
    }
    return(
        <>
        <h1>App Component </h1>
        <h3>My first name is {user1.fname} <br></br>My last name is <span>lname:</span> {user1.lname}</h3>
        <button className='btn btn-primary' onClick={updateObject}>update Object</button>
        </>
    )
}
