import ReactDOM from 'react-dom/client'
import './style.css'
import pic from './images/1.jpg'
const root=ReactDOM.createRoot(document.getElementById('root'))
var result
const dat=new Date()
var h=dat.getHours
if(h<12){
    result="Good Morning"
    var obj={color:'Orange'}
}else if(h>=12 && h<16){
    result="Good Afternoon"
    const obj={color:'Red'}
}else{
    result="Good Evening"
    const obj={color:'Blue'}
}

root.render(
    <>
    <h2>Styling-CSS</h2>
    <br/>
    <h2>Using external style.css in React</h2>
    <br/>
    <h3 style={obj}>result</h3>
    </>
)
